mapper.select:
select

from t_information

	del_flg = '0'

		and t_manager = #{tmanager,jdbcType=VARCHAR}


		and yyb LIKE CONCAT('%',#{yyb,jdbcType=VARCHAR},'%')


		and yyb_manager = #{yybManager,jdbcType=VARCHAR}


		and lc_manager = #{lcManager,jdbcType=VARCHAR}


		and fgs LIKE CONCAT('%',#{fgs,jdbcType=VARCHAR},'%')


		and fgs_manager = #{fgsManager,jdbcType=VARCHAR}


		and dq LIKE CONCAT('%',#{dq,jdbcType=VARCHAR},'%')


		and dq_manager = #{dqManager,jdbcType=VARCHAR}


		and syb LIKE CONCAT('%',#{syb,jdbcType=VARCHAR},'%')


		and syb_manager = #{sybManager,jdbcType=VARCHAR}


		and tender_name LIKE CONCAT('%',#{tenderName,jdbcType=VARCHAR},'%')


		and contract LIKE CONCAT('%',#{contract,jdbcType=CHAR},'%')


		and tel LIKE CONCAT('%',#{tel,jdbcType=VARCHAR},'%')


		and payment_date >=#{startTime}


		and payment_date <=#{endTime}


		and status = #{status,jdbcType=CHAR}


		and continue_flg = #{continueFlg,jdbcType=CHAR}

select

from t_information

	del_flg = '0'

		and start_date >=#{startTime}


		and start_date <=#{endTime}

select
  syb,dq,fgs,count(lc_id) as lcNum,sum(money) as moneySum,sum(jx_achievement) as jxSum
from t_information

	del_flg = '0'

		and payment_date >=#{startTime}


		and payment_date <=#{endTime}


		and jx_achievement >=#{jxAchievement,jdbcType=DECIMAL}


group by syb,dq,fgs
order by syb

select sum(money) as moneySum,payment_date from t_information
del_flg = '0'
and payment_date >=#{startTime}
and payment_date <=#{endTime}
group by payment_date order by payment_date

select sum(money) as moneySum,payment_date from t_information
del_flg = '0'
and payment_date >=#{startTime}
and payment_date <=#{endTime}
group by payment_date order by payment_date

select

from t_information

del_flg = '0'


		status = #{status,jdbcType=CHAR}


		and continue_flg = #{continueFlg,jdbcType=CHAR}
